class Replacing():    def __init__(self, str_):        self.string = str_    def replace_spaces(self):        count_space = 0        for i in range(len(self.string)):            if(self.string[i] == ' '):                count_space = count_space+1            else:                if(count_space > 0):                    temp_end_string = i - count_space                    count_space = 0                    if(temp_end_string < 0):                                temp_end_string = -1 * temp_end_string                    self.string = self.string[:temp_end_string]+'%20'+self.string[temp_end_string:]                                print(self.string)        return self.stringrep = Replacing('"Pradeep Kumar Palagudila"')result = rep.replace_spaces()print('-------')print(result)