class Replacing:    def __init__(self, string_):        self.string_ = string_    def replace_space(self):        count_space = 0        temp = 0        for i in range(len(self.string_)):            print(self.string_[i])            if(self.string_[i] == ' '):                count_space = count_space + 1            elif(self.string_[i] != ' ' and self.string_[i] != '"'):                if(count_space >= 3):                    temp = (i - count_space + 1)                    if(temp < 0):                        temp = temp * -1                    print('temp: ', temp)                    print('String value: ',self.string_[i])                    temp_str = '%20'                    self.string_ = self.string_[:temp]+temp_str+self.string_[temp:]                elif(count_space > 0 and count_space < 3):                    temp = (i - count_space + 1)                    if(temp < 0):                        temp = temp * -1                    print('temp: ', temp)                    print('String value: ',self.string_[i])                    temp_str = '%20'                    print(self.string_[:temp])                    print(temp_str)                    print(self.string_[temp:])                    self.string_ = self.string_[:temp]+temp_str+self.string_[temp:]                count_space = 0        print(self.string_)        return self.string_    def clean_str(self):        temp = ''        self.string_ = self.string_.split(',')        if(len(self.string_)):            for i in self.string_:                temp += i            self.string_ = temp        print(self.string_)        return self.replace_space()func = Replacing('"My John   "')func.clean_str()