"""@author: pradeep"""class DynamicArray:    MAX = 1        def __init__(self):        self.queue = []        self.count = 0        def resize(self):        DynamicArray.MAX = DynamicArray.MAX + 2        temp = self.queue        self.queue = [0] * DynamicArray.MAX        print('DynamicArray.Max', DynamicArray.MAX)        try:            for i in range(len(temp)):                                self.queue[i] = temp[i]            print('After resizing: ', self.queue)        except IndexError:            print(self.queue)    def printall(self):        print(self.queue)    def details(self):        print("Size of the array is: ",DynamicArray.MAX)        print("Number of elements inserted: ", self.count)        self.printall()    def insertion(self, position = None):        value = input('Value entered: ')            copy_max = DynamicArray.MAX        if(len(self.queue) == self.count):            print('Resizing.. \n Done.')            self.resize()        Queue_data_struct = []        position = input('Please enter position to be inserted: ')        while(position < len(self.queue)):            position = input('Please try again, as the size of an array is less than inserted position: ')        for i in range(len(self.queue)):            if(i == position):                Queue_data_struct.append(self.queue[i])                self.queue[i] = value            elif(i > position and i < len(self.queue)):                Queue_data_struct.append(self.queue[i])                        self.queue[i] = Queue_data_struct.pop(0)            elif(i > len(self.queue)):                self.queue.append(Queue_data_struct[i])                        else:            self.queue.insert(self.count, value)                        self.count = self.count + 1                        print('\n')                def deletion(self, position = None):        if(position == 'n'):            pass        if(self.count == 0):            print('No elements in the array')        if(self.count == 1):                        self.queue[0] = 0            self.queue = self.queue[:-1]            self.count = self.count - 1            #DynamicArray.MAX = DynamicArray.MAX - 1         elif(self.count > 1):                        #self.queue = self.queue[i+1:]            print(self.queue[1:])            self.queue = self.queue[1:]            self.count = self.count - 1            #DynamicArray.MAX = DynamicArray.MAX - 1            print('Decreased the size by one \n', len(self.queue))            print('Count: ', self.count)        else:            pass        print(self.queue)da_array = DynamicArray()loop = Truewhile(loop):    print("[1] - Insertion")    print("[2] - deletion")    print("[3] - print all element")    print("[4] - details ")    print("[5] - details \n")    try:        decision = int(input('Enter your decision: '))        if(decision == 1):            position_decision = input('Enter the position: [y/n]: ')            if(position_decision == 'y'):                position = int(input('Enter the position number: '))                da_array.insertion(position)            else:                da_array.insertion()            print("Value entered")        elif(decision == 2):            da_array.deletion()            #position_decision = input('Would you like to enter the position: [y/n]: ')            '''            if(position_decision == 'y'):                            position = int(input('Please enter the position: '))                                    elif(position_decision == 'n'):                da_array.deletion()            '''        elif(decision == 3):            da_array.printall()    except ValueError:        print('Invalid input. Please try again')        da_array.details()    