class DynamicArray:    MAX = 1        def __init__(self):        self.queue = []        self.count = 0        def resize(self):        DynamicArray.MAX = DynamicArray.MAX + 1        temp = self.queue        self.queue = [0] * DynamicArray.MAX        print('DynamicArray.Max', DynamicArray.MAX)        try:            for i in range(len(temp)):                                self.queue[i] = temp[i]            print('After resizing: ', self.queue)        except IndexError:            print(self.queue)    def printall(self):        print(self.queue)    def details(self):        print("Size of the array is: ",DynamicArray.MAX)        print("Number of elements inserted: ", self.count)        self.printall()    def insertion(self):        value = input('Value entered: ')        self.queue.insert(self.count, value)        self.count = self.count + 1        print('\n')        self.resize()    def deletion(self):        if(self.count == 0):            print('No elements in the array')        if(self.count == 1):                        self.queue.pop()                    self.queue.append(0)        elif(self.count > 1):                        for i in range(self.count-1):                self.queue[i] = self.queue[i+1]            self.count = self.count - 1            print('Decreased the size by one \n')            self.queue = self.queue[:-1]            self.queue[-1] = 0                    print(self.queue)da_array = DynamicArray()loop = Truewhile(loop):    print("[1] - Insertion")    print("[2] - deletion")    print("[3] - print all element")    print("[4] - details ")    print("[5] - details \n")    decision = int(input('Enter your decision: '))    if(decision == 1):        da_array.insertion()        print("Value entered")    elif(decision == 2):        da_array.deletion()    elif(decision == 3):        da_array.printall()    elif(decision == 4):        da_array.details()    